document.getElementById("togglePassword").addEventListener("click",(function(){let e=document.getElementById("mailpw"),s=document.getElementById("eyeIcon");"password"===e.type?(e.type="text",s.classList.remove("bi-eye"),s.classList.remove("text-success"),s.classList.add("bi-eye-slash"),s.classList.add("text-danger")):(e.type="password",s.classList.remove("bi-eye-slash"),s.classList.remove("text-danger"),s.classList.add("bi-eye"),s.classList.add("text-success"))}));